import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.css';
import logo from './acexa-white.png';
import './contact.css';
import location from './location.jpg';
import email from './email.jpg';
import call from './th.jpg';
import Finalpage from './finalPage';

const Contact = () => {
  const navigate = useNavigate();
  const [username, setUsername] = useState('');
  const [mailId, setMailId] = useState('');
  const [subject, setSubject] = useState('');
  const [message, setMessage] = useState('');

  const submitForm = (e) => {
    e.preventDefault();
    const userData = JSON.stringify({
      username: username,
      mailId: mailId,
      subject: subject,
      message: message,
    });

    fetch('http://localhost:3001/', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: userData,
    })
      .then((response) => response.json())
      .then((data) => {
        if (data.success) {
          navigate('/finalpage'); // Adjust the URL based on your routing configuration
        } else {
          console.error('Submission failed:', data.error);
        }
      })
      .catch((error) => {
        console.error(error);
      });
  };

  return (
    <div className="container">
      <div className="row">
        <div className="col-md-12 sticky-top">
          <div className="logo nav-bar">
            <img src={logo} alt="" />
            <div className="col-9 d-flex justify-content-end">
              <a href="/">Home</a>
              <a href="/about">About</a>
              <a href="/contact">Contact</a>
            </div>
          </div>
        </div>
        <div className="col-md-6 order-sm-2 form order-md-1">
          <form className="form">
            {/* ... rest of your form */}
            <button type="submit" className="btn btn-primary" onClick={submitForm}>
              Submit
            </button>
          </form>
        </div>
        <div className="col-md-6 order-sm-1 order-md-2 ">
          {/* ... rest of your contact data */}
        </div>
      </div>
    </div>
  );
};

export default Contact;
