import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import logo from './acexa-white.png'; // Import the image
import './contact.css';

const Contact = () => {
  const [username, setUsername] = useState('');
  const [mailId, setMailId] = useState('');
  const [subject, setSubject] = useState('');
  const [message, setMessage] = useState('');
  const submitForm = (e) => {
    e.preventDefault();

    fetch('http://localhost:3001/', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        username: username,
        mailId: mailId,
        subject: subject,
        message: message,
      }),
    })
      .then(response => response.json())
      .then(data => {
        // Handle the response data as needed
        console.log(data);
      })
      .catch(error => {
        // Handle errors
        console.error('Error:', error);
      });
  };

  return (
    <div className="container">
      <nav className="top-nav navbar navbar-expand-lg navbar-light bg-light">
        <img src={logo} alt="Logo" className="navbar-brand logo" />
        <a className="navbar-brand" href="#">h1</a>
        <a className="navbar-brand" href="#">h2</a>
        <a className="navbar-brand" href="#">h3</a>
      </nav>
      <div className="form">
        <input type="text" name="username" value={username} onChange={(e) => setUsername(e.target.value)} placeholder="Your Name"/>
        <input type="text" name="mailId" value={mailId} onChange={(e) => setMailId(e.target.value)} placeholder="Your Mail"/>
        <input type="text" name="subject" value={subject} onChange={(e) => setSubject(e.target.value)} placeholder="Subject"/>
        <textarea name="message" value={message} onChange={(e) => setMessage(e.target.value)} cols="30" rows="5" placeholder="Your Message"/>
        <input type="submit" value="Submit" onClick={submitForm} />
      </div>
    </div>
  );
};

export default Contact;
