import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import logo from './acexa-white.png'; 
import './contact.css';

const Contact = () => {
  const [username, setUsername] = useState('');
  const [mailId, setMailId] = useState('');
  const [subject, setSubject] = useState('');
  const [message, setMessage] = useState('');

  const submitForm = (e) => {
    e.preventDefault();
    const userData = JSON.stringify({
      username: username,
      mailId: mailId,
      subject: subject,
      message: message,
    });

    fetch('http://localhost:3001/', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: userData,
    })
      .then((response) => response.json())
      .then((data) => {
        console.log(data);
      })
      .catch((error) => {
        console.error(error);
      });
  };

  return (
    <div className="container-fluid col-sm-9 ">
        <div className="logo nav-bar">
            <img src={logo} alt="" />
            <div className="col-md-10 d-flex justify-content-end">
            <a href=''>home</a>
            <a href=''>About</a>
            <a href=''>Contact</a>
            </div>
        </div>
      <div className="row justify-content-center col-md-5 ">
          <form className="form">
            <div className="mb-3">
              <input type="text" className="form-control" id="username" value={username} onChange={(e)=>setUsername(e.target.value)} placeholder="Your Name" required />
            </div>
            <div className="mb-3">
              <input type="text" className="form-control" id="mailId" value={mailId} onChange={(e)=>setMailId(e.target.value)} placeholder="Your Mail" required/>
            </div>
            <div className="mb-3">
              <input type="text" className="form-control" id="subject" value={subject} onChange={(e)=>setSubject(e.target.value)} placeholder="Subject" />
            </div>
            <div className="mb-3">
              <textarea className="form-control" id="message" value={message} onChange={(e)=>setMessage(e.target.value)} cols="30" rows="5" placeholder="Message"/>
            </div>
            <button type="submit" className="btn btn-primary" onClick={submitForm}>Submit</button>
          </form>
      </div>
    </div>
  );
};

export default Contact;
